import { StatusBar } from 'expo-status-bar';
import React, { Component, useState, useEffect }  from 'react';
import { StyleSheet, Text, View , Button} from 'react-native';
import ApiEngine  from './comm/ApiEngine';
import CameraCatch from './comm/CameraCatch';
import { Camera } from 'expo-camera';



class App  extends Component {
  constructor(props) {
    super(props);
    this.state = { dataEngineStatus:false, camera: false };

  }
  cancelApi() {
    this.setState({dataEngineStatus:false});
  }
  componentDidMount() {
    // const [hasPermission, setHasPermission] = useState(null);
    /*
    this.setState({dataEngineStatus:'test-' + new Date()});
    const me = this;
    async function f1() {
      var x = await me.resolveAfter2Seconds(10);
      console.log(x); // 10
    }
    f1();
*/
//   let [hasPermission, setHasPermission] = useState(null);
// const [type, setType] = useState(Camera.Constants.Type.back);
    (async () => {
     // const { status } = await Camera.requestPermissionsAsync();
      // setHasPermission(status === 'granted');
    })()
/*
    let types = Camera.getAvailableCameraTypesAsync();
    console.log('====>');
    console.log(types);
*/
  }

  resolveAfter2Seconds(x) {
    return new Promise(resolve => {
      setTimeout(() => {
        resolve(x);
      }, 2000);
    });
  }
  render() {

    if ( Camera.isAvailableAsync()) {
      return (
        <View style={styles.container}>
          {(this.state.camera) && (<CameraCatch/>)}
          <Text>With Camera</Text>


          <View style={styles.buttonContainer}>
            <Button style={styles.buttonStyle} onPress={()=> {
              this.setState({dataEngineStatus:'test-' + new Date()});
            }} title="Pull Data" />
          </View>
          <View style={styles.buttonContainer}>
            <Button style={styles.buttonStyle} onPress={()=> {
              Camera.
              this.setState({camera:(this.state.camera) ? false : true});
            }} title="Switch Camera" />
          </View>


          {(this.state.dataEngineStatus) && (<ApiEngine root={this}/>)} 
        <StatusBar style="auto" />
        </View>
      )
    } else {
      return (
        <View style={styles.container}>
          <Text>NiceJob</Text>
          <Button  style={styles.buttonStyle}  onPress={()=> {
            this.setState({dataEngineStatus:'test-' + new Date()});
          }} title="Pull Data" />

          <Button style={styles.buttonStyle} onPress={()=> {
            this.setState({camera:(this.state.camera) ? false : true});
          }} title="Switch Camera" />

          {(this.state.dataEngineStatus) && (<ApiEngine root={this}/>)} 
          <StatusBar style="auto" />
        </View>
      )
    }
  }
}
export default App;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    color: 'blue',
    backgroundColor: 'transparent',
    // alignItems: 'center',
    justifyContent: 'center',
    height: '100%',
    width: '100%'
  },
  buttonContainer: {
    margin: 6,
  }
});
