// import React in our code
import React from 'react';
// import all the components we are going to use
import { StyleSheet, SafeAreaView, View, FlatList, Image, VirtualizedList } from 'react-native';
import * as FileSystem from 'expo-file-system';
const GridObj =  (data) =>  (
  <SafeAreaView>
    <FlatList
      data={data}
      renderItem={({item}) => (
        <View
          style={{
            flex: 1,
            flexDirection: 'column',
            margin: 1
          }}>
          {item.element}
        </View>
      )}
      //Setting the number of column 
      numColumns={3}
      keyExtractor={(item, index) => index}
    />
  </SafeAreaView>
)
const VirtualizedListObj =  (list) => {
  const getItem = (data, index) => ({
    id: Math.random().toString(12).substring(0),
    title: `${data[index]}`
  });
  const getItemCount = (data) => data.length;
  const Item = ({ title }) => (
    <View style={styles.VirtualizedListItem}>
      <Image style={styles.image} source={{uri:FileSystem.cacheDirectory + 'Camera/' + title}}/>
      {/*<Text style={styles.title}>{title}--</Text>*/}
    </View>
  );
  return (
    <SafeAreaView>
      <VirtualizedList
        data={list}
        initialNumToRender={4}
        renderItem={({ item }) => <Item title={item.title} />}
        keyExtractor={item => item.key}
        getItemCount={getItemCount}
        getItem={getItem}
      />
    </SafeAreaView>
  )
} 
const ListView = (props) => {
  const data = (props.items) ? props.items : [];
  return VirtualizedListObj(data);
};
export default ListView;

const styles = StyleSheet.create({
  VirtualizedListItem: {
    backgroundColor: '#ccc',
    height: 80,
    justifyContent: 'center',
    marginVertical: 6,
    marginHorizontal: 6,
    padding: 10,
  },
  image : {
    width: 80,
    height:80
  },
});
