// import * as React from 'react';
import React, { useState, useEffect, useRef } from 'react';
import { StyleSheet,  View, Text, SafeAreaView, Image, StatusBar } from 'react-native';
import * as FileSystem from 'expo-file-system';
import ListCom from '../comm/ListCom.js';

const PhotosScreen = () => {
  const [list, setList] = useState([]);
//=====
  // const DATA = [];


  useEffect(() => {
    (async () => {
      FileSystem.readDirectoryAsync(FileSystem.cacheDirectory+'Camera/').then((data) => {
        let items = data.map((v, i) => {
          return {
            id: i,
            element : (<Image
              style={styles.imageThumbnail}
              source={{uri: FileSystem.cacheDirectory + 'Camera/' + v}}
            />)
          };
        });
        setList(data);
      });
    })();
  }, []);

  return (
    <SafeAreaView style={{ flex: 1 }}>
      <View style={{ flex: 1, padding: 3 }}>
        <ListCom items={list} type="grid" />
      </View>
    </SafeAreaView>
  );
};
export default PhotosScreen;

const styles = StyleSheet.create({
  cameraFrame: {
    flex: 1,

  },
  image : {
    width: 80,
    height:80
  },
  camera: {
    flex: 1,
    height: '190%',
    width: '100%'
  },
  buttonContainer: {
    flex: 1,
    backgroundColor: 'transparent',
    flexDirection: 'row',
    margin: 20,
  },
  container: {
    flex: 1,
    marginTop: StatusBar.currentHeight,
  },
  VirtualizedListItem: {
    backgroundColor: '#ccc',
    height: 80,
    justifyContent: 'center',
    marginVertical: 6,
    marginHorizontal: 6,
    padding: 10,
  },
  title: {
    fontSize: 32,
  },
  imageThumbnail: {
    justifyContent: 'center',
    alignItems: 'center',
    height: 100,
    margin: 3
  },
});
