import { StatusBar } from 'expo-status-bar';
import React, { Component, useState, useEffect }  from 'react';
import { StyleSheet, Text, View, Button } from 'react-native';
import ApiEngine  from './comm/ApiEngine';

import { Camera } from 'expo-camera';


class App  extends Component {
  constructor(props) {
    super(props);
    this.state = { dataEngineStatus:false };



  }

  cancelApi() {
    this.setState({dataEngineStatus:false});
  }
  componentDidMount() {
    this.setState({dataEngineStatus:'test-' + new Date()});

  }
  render() {
    if ( Camera.isAvailableAsync()) {
      const types = Camera.getAvailableCameraTypesAsync();
      return (
        <View style={styles.container}>
          <Text>YY NiceJob</Text>
          <Button onPress={()=> {
            this.setState({dataEngineStatus:'test-' + new Date()});
          }} title="Pull Data" />
          {(this.state.dataEngineStatus) && (<ApiEngine root={this}/>)} 
          <StatusBar style="auto" />
        </View>
      )
    } else {

      return (
        <View style={styles.container}>
          <Text>NiceJob</Text>
          <Button onPress={()=> {
            this.setState({dataEngineStatus:'test-' + new Date()});
          }} title="Pull Data" />
          {(this.state.dataEngineStatus) && (<ApiEngine root={this}/>)} 
          <StatusBar style="auto" />
        </View>
      )
    }
  //  const [hasPermission, setHasPermission] = useState(null);
  //  const [type, setType] = useState(Camera.Constants.Type.back);


  }
}
export default App;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    color: 'blue',
    backgroundColor: 'transparent',
    alignItems: 'center',
    justifyContent: 'center',
  },
  containera: {
    flex: 1,
    zIndex: 1,
    color: 'blue',
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  }
});
