import React, { useState, useEffect, useRef } from 'react';
import { StyleSheet, Text, View, TouchableOpacity, Alert, Image} from 'react-native';
import {  Button } from 'react-native-elements';
import { Camera } from 'expo-camera';
import CameraRoll from "@react-native-community/cameraroll";
import logo from '../logo.png';
import * as FileSystem from 'expo-file-system';

export default function CameraCatch() {
  const [hasPermission, setHasPermission] = useState(null);
  const [type, setType] = useState(Camera.Constants.Type.back);
  const cameraRef = useRef();
  const [isPreview, setIsPreview] = useState(false);
  const [uri, setUri] = useState(logo);

  useEffect(() => {
    (async () => {
      const { status } = await Camera.requestPermissionsAsync();
      setHasPermission(status === 'granted');
    })();
  }, []);

  const cancelPreview = async () => {
    await cameraRef.current.resumePreview();
    setIsPreview(true);
    setVideoSource(null);
  };

  const renderCancelPreviewButton = () => (
    <TouchableOpacity onPress={cancelPreview} style={styles.closeButton}>
      <View style={[styles.closeCross, { transform: [{ rotate: "45deg" }] }]} />
      <View
        style={[styles.closeCross, { transform: [{ rotate: "-45deg" }] }]}
      />
    </TouchableOpacity>
  );

  const takePicture = async  () => {
   if (cameraRef.current) {
    const options = { quality: 0.5, base64: true, skipProcessing: true };
    const data = await cameraRef.current.takePictureAsync(options);
    const source = data.uri;
    if (source) {
      // await cameraRef.current.pausePreview();
      console.log(source);
      setUri(data);

      setIsPreview(true);


      FileSystem.readDirectoryAsync(FileSystem.documentDirectory).then((freeDiskStorage) => {
        console.log('777')
      //  console.log(freeDiskStorage)
        // Android: 17179869184
        // iOS: 17179869184
      });

      const promiseData  = FileSystem.readDirectoryAsync(FileSystem.documentDirectory);
      console.log('--1->', FileSystem.cacheDirectory + '--Camera/');
      console.log('--2->', FileSystem.documentDirectory + '--Camera/');

      FileSystem.getFreeDiskStorageAsync().then((f) => {
        console.log(f);
      });
      FileSystem.readDirectoryAsync(FileSystem.cacheDirectory+'Camera/').then((f, k) => {
        console.log(f);
        console.log(k);
      });
      Promise.all([promiseData]).then((data) => {
        console.log('222')
        // console.log(FileSystem.documentDirectory.replace('file://', ''));
        console.log(data);
      }).catch((err) => { 
        console.log('1111')
        console.log('catched==>', err) 
      });
      /*
      console.log( 'FileSystem.documentDirectory=>', FileSystem.documentDirectory);
      // CameraRoll.save(data);
      console.log('BBBB');*/
      /*
      Promise.all([promiseData]).then((data) => {
        console.log('222')
        console.log('catched A==>', data);
      }).catch((err) => { 
        console.log('1111')
        console.log('catched==>', err) 
      });
      */
    }
   } else {
    Alert.alert('128');
   }
   return true;
    cameraRef.capture()
      .then((data) => {
        alert('128');

        // console.log(data);
        // this.setState({ path: data.path })
      })
      .catch((err) => {
        alert('129');
        // console.error(err)
      } );

  }


  if (hasPermission === null) {
    return <View/>;
  }
  if (hasPermission === false) {
   return <Text>No access to camera ==??</Text>;
  }
  return (
    <View style={styles.cameraFrame}>
      <Image
          style = {styles.image}
          source={uri}
        />
      <Text>111</Text>
      <Camera style={styles.camera} type={type} ref={cameraRef}>
        <View style={styles.buttonContainer}>
            <Button style={styles.buttonStyle} onPress={()=> {
               setType(
                type === Camera.Constants.Type.back
                  ? Camera.Constants.Type.front
                  : Camera.Constants.Type.back
              );
            }} title="Switch Camera" />
       </View>

       <View style={styles.buttonContainer}>
          <Button style={styles.buttonStyle} onPress={()=> {

              takePicture();
            }} title="Take a picture" />

        </View>
        <View style={styles.container}>
        {isPreview && renderCancelPreviewButton()}
      </View>
      </Camera>
    </View>
  );
}

const styles = StyleSheet.create({
  cameraFrame: {
    flex: 1,

  },
  image : {
    width: 200,
    height:200
  },
  camera: {
    flex: 1,
    height: '190%',
    width: '100%'
  },
  buttonContainer: {
    flex: 1,
    backgroundColor: 'transparent',
    flexDirection: 'row',
    margin: 20,
  }
});
