import { StatusBar } from 'expo-status-bar';
import React, { Component }  from 'react';
import { StyleSheet, Text, View, Button } from 'react-native';
import ApiEngine  from './comm/ApiEngine';

class App  extends Component {
  constructor(props) {
    super(props);
    this.state = { apiCall:false };
  }

  cancelApi() {
    this.setState({apiCall:false});
  }
  componentDidMount() {
    this.setState({apiCall:'test-' + new Date()});
  }
  render() {
    return (
      <View style={styles.container}>
        <Text>NiceJob</Text>
        <Button onPress={()=> {
          this.setState({apiCall:'test-' + new Date()});
        }} title="Learn More">BTN</Button>
        {(this.state.apiCall) && (<ApiEngine root={this}/>)} 
        <StatusBar style="auto" />
      </View>
    )
  }
}
export default App;
// let ScreenHeight = Dimensions.get("window").height;
const styles = StyleSheet.create({
  container: {
    flex: 1,
    color: 'blue',
    backgroundColor: 'transparent',
    alignItems: 'center',
    justifyContent: 'center',
  },
  containera: {
    flex: 1,
    zIndex: 1,
    color: 'blue',
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  }
});
