import { StatusBar } from 'expo-status-bar';
import React, { Component, useState, useEffect }  from 'react';
import { StyleSheet, Text, View, Button } from 'react-native';
import ApiEngine  from './comm/ApiEngine';

import { Camera } from 'expo-camera';



class App  extends Component {
  constructor(props) {
    super(props);
    this.state = { dataEngineStatus:false };

  }
  cancelApi() {
    this.setState({dataEngineStatus:false});
  }
  componentDidMount() {
    /*
    this.setState({dataEngineStatus:'test-' + new Date()});
    const me = this;
    async function f1() {
      var x = await me.resolveAfter2Seconds(10);
      console.log(x); // 10
    }
    f1();
*/
//   let [hasPermission, setHasPermission] = useState(null);
// const [type, setType] = useState(Camera.Constants.Type.back);
    (async () => {
      const { status } = await Camera.requestPermissionsAsync();
      // setHasPermission(status === 'granted');
    })()
/*
    let types = Camera.getAvailableCameraTypesAsync();
    console.log('====>');
    console.log(types);
*/
  }

  resolveAfter2Seconds(x) {
    return new Promise(resolve => {
      setTimeout(() => {
        resolve(x);
      }, 2000);
    });
  }
  render() {
    let [hasPermission, setHasPermission] = useState(null);
    if ( Camera.isAvailableAsync()) {
      let types = Camera.getAvailableCameraTypesAsync();
      console.log('====>');
      console.log(types);
      return (
        <View style={styles.container}>
          <Text>YY NiceJob -- {types.title}</Text>
          <Button onPress={()=> {
            this.setState({dataEngineStatus:'test-' + new Date()});
          }} title="Pull Data" />
          {(this.state.dataEngineStatus) && (<ApiEngine root={this}/>)} 
          <StatusBar style="auto" />
        </View>
      )
    } else {

      return (
        <View style={styles.container}>
          <Text>NiceJob</Text>
          <Button onPress={()=> {
            this.setState({dataEngineStatus:'test-' + new Date()});
          }} title="Pull Data" />
          {(this.state.dataEngineStatus) && (<ApiEngine root={this}/>)} 
          <StatusBar style="auto" />
        </View>
      )
    }
  }
}
export default App;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    color: 'blue',
    backgroundColor: 'transparent',
    alignItems: 'center',
    justifyContent: 'center',
  },
  containera: {
    flex: 1,
    zIndex: 1,
    color: 'blue',
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  }
});
